INSERT INTO `wp_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`, `layout`, `image`, `is_deleted`, `icon`, `is_read`) VALUES
(1, 'wc-refund-returns-page', 'info', 'en_US', 'Setup a Refund and Returns Policy page to boost your store\'s credibility.', 'We have created a sample draft Refund and Returns Policy page for you. Please have a look and update it to fit your store.', '{}', 'unactioned', 'woocommerce-core', '2021-12-03 08:13:33', NULL, 0, 'plain', '', 0, 'info', 1),
(2, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-03 08:13:33', NULL, 0, 'plain', '', 0, 'info', 1),
(3, 'wc-admin-learn-more-about-variable-products', 'info', 'en_US', 'Learn more about variable products', 'Variable products are a powerful product type that lets you offer a set of variations on a product, with control over prices, stock, image and more for each variation. They can be used for a product like a shirt, where you can offer a large, medium and small and in different colors.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-03 09:42:31', NULL, 0, 'plain', '', 0, 'info', 1),
(4, 'wc-admin-test-checkout', 'info', 'en_US', 'Đừng quên kiểm tra chức năng thanh toán', 'Make sure that your checkout is working properly before you launch your store. Go through your checkout process in its entirety: from adding a product to your cart, choosing a shipping location, and making a payment.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-03 10:04:03', NULL, 0, 'plain', '', 0, 'info', 1),
(5, 'wc-admin-orders-milestone', 'info', 'en_US', 'First order received', 'Congratulations on getting your first order! Now is a great time to learn how to manage your orders.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-03 12:13:40', NULL, 0, 'plain', '', 0, 'info', 0),
(6, 'wc-admin-coupon-page-moved', 'update', 'en_US', 'Coupon management has moved!', 'Coupons can now be managed from Marketing &gt; Coupons. Click the button below to remove the legacy WooCommerce &gt; Coupons menu item.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-03 12:42:58', NULL, 0, 'plain', '', 0, 'info', 0),
(7, 'wc-admin-personalize-store', 'info', 'en_US', 'Personalize your store\'s homepage', 'The homepage is one of the most important entry points in your store. When done right it can lead to higher conversions and engagement. Don\'t forget to personalize the homepage that we created for your store during the onboarding.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-04 09:28:11', NULL, 0, 'plain', '', 0, 'info', 0),
(8, 'wc-admin-launch-checklist', 'info', 'en_US', 'Ready to launch your store?', 'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-04 09:28:13', NULL, 0, 'plain', '', 0, 'info', 0),
(9, 'wc-admin-filter-by-product-variations-in-reports', 'info', 'en_US', 'New - filter by product variations in orders and products reports', 'One of the most awaited features has just arrived! You can now have insights into each product variation in the orders and products reports.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-04 09:28:13', NULL, 0, 'banner', 'http://localhost/wordpress/wp-content/plugins/woocommerce/packages/woocommerce-admin/images/admin_notes/filter-by-product-variations-note.svg', 0, 'info', 0),
(10, 'wc-admin-insight-first-product-and-payment', 'survey', 'en_US', 'Insight', 'More than 80% of new merchants add the first product and have at least one payment method set up during the first week.<br><br>Do you find this type of insight useful?', '{}', 'unactioned', 'woocommerce-admin', '2021-12-04 09:28:13', NULL, 0, 'plain', '', 0, 'info', 0),
(11, 'wc-admin-first-downloadable-product', 'info', 'en_US', 'Learn more about digital/downloadable products', 'Congrats on adding your first digital product! You can learn more about how to handle digital or downloadable products in our documentation.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-04 09:28:13', NULL, 0, 'plain', '', 0, 'info', 0),
(12, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-09 07:58:59', NULL, 0, 'plain', '', 0, 'info', 0),
(13, 'wc-admin-marketing-intro', 'info', 'en_US', 'Kết nối với khách hàng tiềm năng', 'Tăng khách hàng tiềm năng và số lượng đơn hàng với các công cụ marketing dành cho WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-09 07:58:59', NULL, 0, 'plain', '', 0, 'info', 0),
(14, 'wc-admin-need-some-inspiration', 'info', 'en_US', 'Browse our success stories', 'Learn more about how other entrepreneurs used WooCommerce to build successful businesses.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-09 07:58:59', NULL, 0, 'plain', '', 0, 'info', 0),
(15, 'wc-admin-online-clothing-store', 'info', 'en_US', 'Start your online clothing store', 'Starting a fashion website is exciting but it may seem overwhelming as well. In this article, we\'ll walk you through the setup process, teach you to create successful product listings, and show you how to market to your ideal audience.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-09 07:58:59', NULL, 0, 'plain', '', 0, 'info', 0),
(16, 'wc-admin-draw-attention', 'info', 'en_US', 'Get noticed: how to draw attention to your online store', 'To get you started, here are seven ways to boost your sales and avoid getting drowned out by similar, mass-produced products competing for the same buyers.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-09 07:58:59', NULL, 0, 'plain', '', 0, 'info', 0),
(17, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2021-12-09 07:58:59', NULL, 0, 'plain', '', 0, 'info', 0),
(18, 'wc-admin-insight-first-sale', 'survey', 'en_US', 'Did you know?', 'A WooCommerce powered store needs on average 31 days to get the first sale. You\'re on the right track! Do you find this type of insight useful?', '{}', 'unactioned', 'woocommerce-admin', '2021-12-11 05:16:01', NULL, 0, 'plain', '', 0, 'info', 0),
(19, 'wc-update-db-reminder', 'update', 'en_US', 'Yêu cầu cập nhật cơ sở dữ liệu WooCommerce', 'WooCommerce đã được cập nhật! Để đảm bảo mọi thứ hoạt động trơn tru, chúng tôi cần cập nhật cơ sở dữ liệu lên bản mới nhất. Quá trình cập nhật cơ sở dữ liệu sẽ chạy nền và sẽ mất chút thời gian, hãy kiên nhẫn. Người dùng cao cấp có thể cập nhật thay thế qua <a href=\"https://github.com/woocommerce/woocommerce/wiki/Upgrading-the-database-using-WP-CLI\">WP CLI</a>.', '{}', 'unactioned', 'woocommerce-core', '2022-01-12 00:36:44', NULL, 0, 'plain', '', 0, 'info', 0);
